    if (!existingPlan) return undefined;

    const updatedPlan: DailyPlan = { ...existingPlan, ...plan };
    this.dailyPlans.set(id, updatedPlan);
    return updatedPlan;
  }

  async deleteDailyPlan(id: string): Promise<boolean> {
    return this.dailyPlans.delete(id);
  }
}

