          <DialogTitle className="flex items-center space-x-2">
            <SelectedIcon className="h-5 w-5" style={{ color: selectedColor }} />
            <span>Create New Habit</span>
          </DialogTitle>
          <DialogDescription>
            Build a custom habit with flexible tracking options and personalized settings.
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="template" data-testid="tab-template">Quick Start</TabsTrigger>
            <TabsTrigger value="customize" data-testid="tab-customize">Customize</TabsTrigger>
            <TabsTrigger value="advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
          </TabsList>

          <TabsContent value="template" className="space-y-6">
            <div>
              <h3 className="text-lg font-semibold mb-4">Choose a Template</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {HABIT_TEMPLATES.map((template) => {
                  const IconComponent = ICONS.find(icon => icon.id === template.icon)?.component || Target;
                  return (
                    <Card 
                      key={template.id} 
                      className="cursor-pointer hover:shadow-md transition-shadow"
                      onClick={() => applyTemplate(template)}
                      data-testid={`template-${template.id}`}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-start space-x-3">
                          <div 
                            className="w-10 h-10 rounded-lg flex items-center justify-center text-white"
                            style={{ backgroundColor: template.color }}
                          >
                            <IconComponent className="h-5 w-5" />
                          </div>
                          <div className="flex-1">
                            <h4 className="font-semibold">{template.name}</h4>
                            <p className="text-sm text-gray-600 mb-2">{template.description}</p>
                            <div className="flex flex-wrap gap-1">
                              <Badge variant="secondary" className="text-xs">
                                {template.trackingType}
                              </Badge>
                              {template.targetValue && (
                                <Badge variant="outline" className="text-xs">
                                  {template.targetValue} {template.unit}
                                </Badge>
                              )}
                              <Badge variant="outline" className="text-xs">
                                {template.frequency}
                              </Badge>
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            </div>

            <div className="text-center">
              <Button 
                variant="outline" 
                onClick={() => setActiveTab("customize")}
                data-testid="button-custom-habit"
              >
                <Palette className="h-4 w-4 mr-2" />
                Create Custom Habit
              </Button>
            </div>
          </TabsContent>

          <TabsContent value="customize" className="space-y-6">
            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Basic Information */}
              <div className="space-y-4">
                <div>
                  <Label htmlFor="habit-name">Habit Name *</Label>
                  <Input
                    id="habit-name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="e.g., Morning meditation"
                    required
                    data-testid="input-habit-name"
                  />
                </div>

                <div>
                  <Label htmlFor="habit-description">Description</Label>
                  <Textarea
                    id="habit-description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Optional description..."
                    rows={2}
                    data-testid="textarea-habit-description"
                  />
                </div>
              </div>

              <Separator />

              {/* Tracking Type */}
              <div className="space-y-4">
                <div>
                  <Label>Tracking Type</Label>
                  <Select value={trackingType} onValueChange={(value: any) => setTrackingType(value)}>
                    <SelectTrigger data-testid="select-tracking-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="boolean">Simple (Done/Not Done)</SelectItem>
                      <SelectItem value="numeric">Numeric (Count or Amount)</SelectItem>
                      <SelectItem value="duration">Duration (Time-based)</SelectItem>
                      <SelectItem value="custom">Custom Tracking</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {trackingType !== "boolean" && (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label>Unit of Measurement</Label>
                      <Select value={unit} onValueChange={setUnit}>
                        <SelectTrigger data-testid="select-unit">
                          <SelectValue placeholder="Choose unit..." />
                        </SelectTrigger>
                        <SelectContent>
                          {UNITS.map((u) => (
                            <SelectItem key={u.value} value={u.value}>
                              {u.label}
                            </SelectItem>
                          ))}
                          <SelectItem value="custom">Custom Unit</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    {unit === "custom" && (
                      <div>
                        <Label>Custom Unit</Label>
                        <Input
                          value={customUnit}
                          onChange={(e) => setCustomUnit(e.target.value)}
                          placeholder="e.g., chapters, workouts..."
                          data-testid="input-custom-unit"
                        />
                      </div>
                    )}

                    <div>
                      <Label>Target Value</Label>
                      <Input
                        type="number"
                        value={targetValue || ""}
                        onChange={(e) => setTargetValue(parseFloat(e.target.value) || null)}
                        placeholder="e.g., 8, 30, 10000"
                        min="0"
                        step="0.1"
                        data-testid="input-target-value"
                      />
                    </div>
                  </div>
                )}
              </div>

              <Separator />

              {/* Frequency */}
              <div className="space-y-4">
                <div>
                  <Label>Frequency</Label>
                  <Select value={frequency} onValueChange={(value: any) => setFrequency(value)}>
                    <SelectTrigger data-testid="select-frequency">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="daily">Daily</SelectItem>
                      <SelectItem value="weekly">Weekly (Select Days)</SelectItem>
                      <SelectItem value="custom">Custom Schedule</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {(frequency === "weekly" || frequency === "custom") && (
                  <div>
                    <Label>Select Days</Label>
                    <div className="flex flex-wrap gap-2 mt-2">
                      {WEEKDAYS.map((day) => (
                        <Button
                          key={day.id}
                          type="button"
                          variant={selectedDays.includes(day.id) ? "default" : "outline"}
                          size="sm"
                          onClick={() => toggleDay(day.id)}
                          data-testid={`day-${day.id}`}
                        >
                          {day.label}
                        </Button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <Separator />

              {/* Visual Customization */}
              <div className="space-y-4">
                <div>
                  <Label>Icon</Label>
                  <div className="grid grid-cols-8 gap-2 mt-2">
                    {ICONS.map((icon) => {
                      const IconComponent = icon.component;
                      return (
                        <Button
                          key={icon.id}
                          type="button"
                          variant={selectedIcon === icon.id ? "default" : "outline"}
                          size="sm"
                          className="aspect-square p-0"
                          onClick={() => setSelectedIcon(icon.id)}
                          data-testid={`icon-${icon.id}`}
                        >
                          <IconComponent className="h-4 w-4" />
                        </Button>
                      );
                    })}
                  </div>
                </div>

                <div>
                  <Label>Color</Label>
                  <div className="grid grid-cols-10 gap-2 mt-2">
                    {COLORS.map((color) => (
                      <Button
                        key={color}
                        type="button"
                        className="aspect-square p-0 border-2"
                        style={{ 
                          backgroundColor: color,
                          borderColor: selectedColor === color ? "#000" : "transparent"
                        }}
                        onClick={() => setSelectedColor(color)}
                        data-testid={`color-${color}`}
                      />
                    ))}
                  </div>
                </div>
              </div>

              <div className="flex justify-end space-x-3 pt-6">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => onOpenChange(false)}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={!name.trim() || createHabitMutation.isPending}
                  data-testid="button-create-habit"
                >
                  {createHabitMutation.isPending ? "Creating..." : "Create Habit"}
                </Button>
              </div>
            </form>
          </TabsContent>

          <TabsContent value="advanced" className="space-y-6">
            <div className="text-center py-12">
              <div className="max-w-2xl mx-auto">
                <h3 className="text-lg font-semibold mb-4">Advanced Features</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 text-left">
                  <Card>
                    <CardContent className="p-4">
                      <h4 className="font-semibold mb-2">âœ¨ Flexible Tracking</h4>
                      <p className="text-sm text-gray-600">
                        Track anything with custom units: glasses of water, pages read, 
                        miles run, meditation minutes, or create your own measurement.
                      </p>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-4">
                      <h4 className="font-semibold mb-2">ðŸ“… Custom Schedules</h4>
                      <p className="text-sm text-gray-600">
                        Set daily, weekly, or completely custom schedules. Perfect for 
                        habits that don't happen every day.
                      </p>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-4">
                      <h4 className="font-semibold mb-2">ðŸŽ¯ Goal Setting</h4>
                      <p className="text-sm text-gray-600">
                        Set specific targets for numeric habits. Track progress toward 
                        daily, weekly, or custom goals.
                      </p>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-4">
                      <h4 className="font-semibold mb-2">ðŸŽ¨ Visual Customization</h4>
                      <p className="text-sm text-gray-600">
                        Personalize with icons and colors. Make your habits visually 
                        distinct and motivating.
                      </p>
                    </CardContent>
                  </Card>
                </div>

                <div className="mt-8">
                  <Button 
                    onClick={() => setActiveTab("customize")}
                    data-testid="button-start-customizing"
                  >
                    Start Customizing
                  </Button>
                </div>
              </div>
            </div>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/dialogs/log-health.content:
import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { HealthEntry } from "@shared/schema";

interface LogHealthDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  existingEntry?: HealthEntry;
}

export default function LogHealthDialog({ open, onOpenChange, existingEntry }: LogHealthDialogProps) {
  const [sleepHours, setSleepHours] = useState(7);
  const [sleepQuality, setSleepQuality] = useState([8]);
  const [exerciseMinutes, setExerciseMinutes] = useState(0);
  const [exerciseType, setExerciseType] = useState("");
  const [caloriesBurned, setCaloriesBurned] = useState(0);
  const [mood, setMood] = useState([7]);
  const [notes, setNotes] = useState("");

  const { toast } = useToast();
  const queryClient = useQueryClient();
  const today = new Date().toISOString().split('T')[0];

  // Load existing entry data
  useEffect(() => {
    if (existingEntry) {
      setSleepHours(existingEntry.sleepHours || 7);
      setSleepQuality([existingEntry.sleepQuality || 8]);
      setExerciseMinutes(existingEntry.exerciseMinutes || 0);
      setExerciseType(existingEntry.exerciseType || "");
      setCaloriesBurned(existingEntry.caloriesBurned || 0);
      setMood([existingEntry.mood || 7]);
      setNotes(existingEntry.notes || "");
    }
  }, [existingEntry]);

  const saveHealthMutation = useMutation({
    mutationFn: async (data: any) => {
      if (existingEntry) {
        return apiRequest("PUT", `/api/health-entries/${existingEntry.id}`, data);
      } else {
        return apiRequest("POST", "/api/health-entries", { ...data, date: today });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/health-entries'] });
      toast({
        title: "Health data saved",
        description: "Your health metrics have been updated.",
      });
      onOpenChange(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save health data. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    saveHealthMutation.mutate({
      sleepHours: sleepHours > 0 ? sleepHours : undefined,
      sleepQuality: sleepQuality[0],
      exerciseMinutes: exerciseMinutes > 0 ? exerciseMinutes : undefined,
      exerciseType: exerciseType.trim() || undefined,
      caloriesBurned: caloriesBurned > 0 ? caloriesBurned : undefined,
      mood: mood[0],
      notes: notes.trim() || undefined,
    });
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Log Health Data</DialogTitle>
          <DialogDescription>
            Record your daily health metrics including sleep, exercise, and mood.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="sleep-hours">Sleep Hours</Label>
            <Input
              id="sleep-hours"
              type="number"
              step="0.5"
              value={sleepHours}
              onChange={(e) => setSleepHours(parseFloat(e.target.value) || 0)}
              placeholder="7.5"
              min="0"
              max="24"
              data-testid="input-sleep-hours"
            />
          </div>

          <div>
            <Label>Sleep Quality: {sleepQuality[0]}/10</Label>
            <Slider
              value={sleepQuality}
              onValueChange={setSleepQuality}
              max={10}
              min={1}
              step={1}
              className="mt-2"
              data-testid="slider-sleep-quality"
            />
          </div>

          <div>
            <Label htmlFor="exercise-minutes">Exercise Duration (minutes)</Label>
            <Input
              id="exercise-minutes"
              type="number"
              value={exerciseMinutes}
              onChange={(e) => setExerciseMinutes(parseInt(e.target.value) || 0)}
              placeholder="30"
              min="0"
              data-testid="input-exercise-minutes"
            />
          </div>

          <div>
            <Label htmlFor="exercise-type">Exercise Type</Label>
            <Select value={exerciseType} onValueChange={setExerciseType}>
              <SelectTrigger data-testid="select-exercise-type">
                <SelectValue placeholder="Select exercise type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">None</SelectItem>
                <SelectItem value="cardio">Cardio</SelectItem>
                <SelectItem value="strength">Strength Training</SelectItem>
                <SelectItem value="yoga">Yoga</SelectItem>
                <SelectItem value="running">Running</SelectItem>
                <SelectItem value="swimming">Swimming</SelectItem>
                <SelectItem value="cycling">Cycling</SelectItem>
                <SelectItem value="walking">Walking</SelectItem>
                <SelectItem value="other">Other</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="calories-burned">Calories Burned</Label>
            <Input
              id="calories-burned"
              type="number"
              value={caloriesBurned}
              onChange={(e) => setCaloriesBurned(parseInt(e.target.value) || 0)}
              placeholder="250"
              min="0"
              data-testid="input-calories-burned"
            />
          </div>

          <div>
            <Label>Mood: {mood[0]}/10</Label>
            <Slider
              value={mood}
              onValueChange={setMood}
              max={10}
              min={1}
              step={1}
              className="mt-2"
              data-testid="slider-mood"
            />
          </div>

          <div>
            <Label htmlFor="health-notes">Notes</Label>
            <Textarea
              id="health-notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="How are you feeling today?"
              data-testid="textarea-health-notes"
            />
          </div>

          <div className="flex justify-end space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-health"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={saveHealthMutation.isPending}
              data-testid="button-save-health"
            >
              {saveHealthMutation.isPending ? "Saving..." : "Save"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/habits/habit-entry.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Check, Plus, Minus, Target, Clock } from "lucide-react";
import type { Habit, HabitEntry } from "@shared/schema";

interface HabitEntryFormProps {
  habit: Habit;
  existingEntry?: HabitEntry;
  date: string;
  onComplete?: () => void;
  compact?: boolean;
}

export default function HabitEntryForm({ 
  habit, 
  existingEntry, 
  date, 
  onComplete,
  compact = false 
}: HabitEntryFormProps) {
  const [showDialog, setShowDialog] = useState(false);
  const [value, setValue] = useState(existingEntry?.value || 0);
  const [notes, setNotes] = useState(existingEntry?.notes || "");
  const [completed, setCompleted] = useState(existingEntry?.completed || false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateEntryMutation = useMutation({
    mutationFn: async (data: any) => {
      if (existingEntry) {
        return apiRequest("PUT", `/api/habit-entries/${existingEntry.id}`, data);
      } else {
        return apiRequest("POST", "/api/habit-entries", {
          habitId: habit.id,
          date,
          ...data
        });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/habit-entries'] });
      queryClient.invalidateQueries({ queryKey: ['/api/habits'] });
      toast({
        title: "Progress updated",
        description: "Your habit progress has been saved.",
      });
      setShowDialog(false);
      onComplete?.();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update progress. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleQuickUpdate = (newValue: number, isCompleted?: boolean) => {
    const finalCompleted = isCompleted ?? (habit.targetValue ? newValue >= habit.targetValue : newValue > 0);
    updateEntryMutation.mutate({
      value: newValue,
      completed: finalCompleted,
      notes
    });
    setValue(newValue);
    setCompleted(finalCompleted);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const finalCompleted = habit.trackingType === "boolean" 
      ? completed 
      : (habit.targetValue ? value >= habit.targetValue : value > 0);

    updateEntryMutation.mutate({
      value: habit.trackingType === "boolean" ? (completed ? 1 : 0) : value,
      completed: finalCompleted,
      notes: notes.trim() || null
    });
  };

  const getProgressPercentage = () => {
    if (habit.trackingType === "boolean") {
      return completed ? 100 : 0;
    }
    if (habit.targetValue && habit.targetValue > 0) {
      return Math.min((value / habit.targetValue) * 100, 100);
    }
    return value > 0 ? 100 : 0;
  };

  const formatValue = (val: number) => {
    if (habit.trackingType === "duration") {
      const hours = Math.floor(val / 60);
      const mins = val % 60;
      return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
    }
    return val.toString();
  };

  // For compact display (dashboard)
  if (compact) {
    if (habit.trackingType === "boolean") {
      return (
        <button
          onClick={() => handleQuickUpdate(completed ? 0 : 1, !completed)}
          disabled={updateEntryMutation.isPending}
          className={`w-6 h-6 rounded-full flex items-center justify-center transition-all ${
            completed
              ? "bg-secondary text-white"
              : "border-2 border-gray-300 hover:border-secondary"
          }`}
          data-testid={`button-toggle-habit-${habit.id}`}
        >
          {completed && <Check className="h-3 w-3" />}
        </button>
      );
    }

    return (
      <div className="space-y-2">
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => value > 0 && handleQuickUpdate(value - 1)}
            disabled={value <= 0 || updateEntryMutation.isPending}
            data-testid={`button-decrease-${habit.id}`}
          >
            <Minus className="h-3 w-3" />
          </Button>

          <span className="text-sm font-medium min-w-[60px] text-center">
            {formatValue(value)} {habit.unit}
          </span>

          <Button
            variant="outline"
            size="sm"
            onClick={() => handleQuickUpdate(value + (habit.trackingType === "duration" ? 5 : 1))}
            disabled={updateEntryMutation.isPending}
            data-testid={`button-increase-${habit.id}`}
          >
            <Plus className="h-3 w-3" />
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowDialog(true)}
            data-testid={`button-details-${habit.id}`}
          >
            â‹¯
          </Button>
        </div>

        {habit.targetValue && (
          <div className="space-y-1">
            <Progress value={getProgressPercentage()} className="h-2" />
            <div className="text-xs text-gray-500 text-center">
              {formatValue(value)} / {formatValue(habit.targetValue)} {habit.unit}
            </div>
          </div>
        )}

        <Dialog open={showDialog} onOpenChange={setShowDialog}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{habit.name}</DialogTitle>
              <DialogDescription>Update your progress for {date}</DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="value">
                  {habit.trackingType === "duration" ? "Duration (minutes)" : `Amount (${habit.unit})`}
                </Label>
                <Input
                  id="value"
                  type="number"
                  value={value}
                  onChange={(e) => setValue(parseFloat(e.target.value) || 0)}
                  min="0"
                  step={habit.trackingType === "duration" ? "5" : "0.1"}
                  data-testid="input-habit-value"
                />
              </div>

              <div>
                <Label htmlFor="notes">Notes (optional)</Label>
                <Textarea
                  id="notes"
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                  rows={2}
                  placeholder="Add notes about your progress..."
                  data-testid="textarea-habit-notes"
                />
              </div>

              <div className="flex justify-end space-x-2">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setShowDialog(false)}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={updateEntryMutation.isPending}
                  data-testid="button-save-habit-entry"
                >
                  {updateEntryMutation.isPending ? "Saving..." : "Save"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>
    );
  }

  // Full form display (habits page)
  return (
    <div className="space-y-4 p-4 border rounded-lg">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div 
            className="w-8 h-8 rounded-lg flex items-center justify-center text-white text-sm"
            style={{ backgroundColor: habit.color || "#1976D2" }}
          >
            {habit.icon === "target" ? <Target className="h-4 w-4" /> : 
             habit.icon === "clock" ? <Clock className="h-4 w-4" /> : "ðŸ“"}
          </div>
          <div>
            <h4 className="font-semibold">{habit.name}</h4>
            <div className="flex items-center space-x-2 text-sm text-gray-600">
              <Badge variant="secondary">{habit.trackingType}</Badge>
              {habit.frequency !== "daily" && (
                <Badge variant="outline">{habit.frequency}</Badge>
              )}
            </div>
          </div>
        </div>

        {habit.trackingType === "boolean" ? (
          <Button
            onClick={() => handleQuickUpdate(completed ? 0 : 1, !completed)}
            disabled={updateEntryMutation.isPending}
            variant={completed ? "default" : "outline"}
            size="sm"
            data-testid={`button-toggle-habit-${habit.id}`}
          >
            <Check className="h-4 w-4 mr-1" />
            {completed ? "Done" : "Mark Done"}
          </Button>
        ) : (
          <div className="flex items-center space-x-2">
            <span className="text-lg font-semibold">
              {formatValue(value)} {habit.unit}
            </span>
            <div className="flex items-center space-x-1">
              <Button
                variant="outline"
                size="sm"
                onClick={() => value > 0 && handleQuickUpdate(value - (habit.trackingType === "duration" ? 5 : 1))}
                disabled={value <= 0 || updateEntryMutation.isPending}
                data-testid={`button-decrease-${habit.id}`}
              >
                <Minus className="h-3 w-3" />
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={() => handleQuickUpdate(value + (habit.trackingType === "duration" ? 5 : 1))}
                disabled={updateEntryMutation.isPending}
                data-testid={`button-increase-${habit.id}`}
              >
                <Plus className="h-3 w-3" />
              </Button>
            </div>
          </div>
        )}
      </div>

      {habit.targetValue && (
        <div className="space-y-2">
          <div className="flex justify-between text-sm">
            <span>Progress</span>
            <span>{Math.round(getProgressPercentage())}%</span>
          </div>
          <Progress value={getProgressPercentage()} className="h-2" />
          <div className="text-xs text-gray-500 text-center">
            Target: {formatValue(habit.targetValue)} {habit.unit}
          </div>
        </div>
      )}

      {notes && (
        <div className="text-sm text-gray-600 bg-gray-50 p-2 rounded">
          <strong>Notes:</strong> {notes}
        </div>
      )}

      <Button
        variant="ghost"
        size="sm"
        onClick={() => setShowDialog(true)}
        className="w-full"
        data-testid={`button-edit-details-${habit.id}`}
      >
        Edit Details
      </Button>

      <Dialog open={showDialog} onOpenChange={setShowDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{habit.name}</DialogTitle>
            <DialogDescription>Update your progress for {date}</DialogDescription>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-4">
            {habit.trackingType === "boolean" ? (
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="completed"
                  checked={completed}
                  onChange={(e) => setCompleted(e.target.checked)}
                  className="rounded"
                  data-testid="checkbox-habit-completed"
                />
                <Label htmlFor="completed">Mark as completed</Label>
              </div>
            ) : (
              <div>
                <Label htmlFor="value">
                  {habit.trackingType === "duration" ? "Duration (minutes)" : `Amount (${habit.unit})`}
                </Label>
                <Input
                  id="value"
                  type="number"
                  value={value}
                  onChange={(e) => setValue(parseFloat(e.target.value) || 0)}
                  min="0"
                  step={habit.trackingType === "duration" ? "5" : "0.1"}
                  data-testid="input-habit-value"
                />
              </div>
            )}

            <div>
              <Label htmlFor="notes">Notes (optional)</Label>
              <Textarea
                id="notes"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                rows={3}
                placeholder="Add notes about your progress, challenges, or insights..."
                data-testid="textarea-habit-notes"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setShowDialog(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={updateEntryMutation.isPending}
                data-testid="button-save-habit-entry"
              >
                {updateEntryMutation.isPending ? "Saving..." : "Save Progress"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

