import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { 
  Play, 
  Plus, 
  Download,
  CheckCircle2,
  Clock,
  Calendar,
  Trash2,
  Edit,
  Link as LinkIcon,
  Star,
  Filter
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function WatchlistPage() {
  const { toast } = useToast();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingItem, setEditingItem] = useState<any>(null);
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [filterType, setFilterType] = useState<string>("all");
  const [newItem, setNewItem] = useState({
    title: "",
    type: "movie",
    source: "",
    link: "",
    length: "",
    notes: ""
  });

  const { data: watchlistItems = [] } = useQuery({
    queryKey: ['/api/watchlist'],
    queryFn: () => fetch('/api/watchlist').then(res => res.json())
  });

  // Mutations
  const addItemMutation = useMutation({
    mutationFn: (data: any) => apiRequest("POST", "/api/watchlist", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/watchlist'] });
      setIsAddDialogOpen(false);
      resetNewItem();
      toast({ title: "Item added!", description: "Added to your watchlist." });
    }
  });

  const updateItemMutation = useMutation({
    mutationFn: (data: { id: string; updates: any }) => 
      apiRequest("PUT", `/api/watchlist/${data.id}`, data.updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/watchlist'] });
      setEditingItem(null);
      toast({ title: "Item updated!", description: "Watchlist item updated successfully." });
    }
  });

  const deleteItemMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/watchlist/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/watchlist'] });
      toast({ title: "Item deleted", description: "Removed from your watchlist." });
    }
  });

  const resetNewItem = () => {
    setNewItem({
      title: "",
      type: "movie",
      source: "",
      link: "",
      length: "",
      notes: ""
    });
  };

  const handleMarkDone = (item: any) => {
    updateItemMutation.mutate({
      id: item.id,
      updates: { status: "Done" }
    });
  };

  const handleStatusChange = (item: any, newStatus: string) => {
    updateItemMutation.mutate({
      id: item.id,
      updates: { status: newStatus }
    });
  };

  // Calculate stats
  const stats = {
    total: watchlistItems.length,
    toWatch: watchlistItems.filter((item: any) => item.status === "To Watch").length,
    inProgress: watchlistItems.filter((item: any) => item.status === "In Progress").length,
    done: watchlistItems.filter((item: any) => item.status === "Done").length,
    thisWeek: watchlistItems.filter((item: any) => {
      if (!item.finishedAt) return false;
      const finishedDate = new Date(item.finishedAt);
      const weekAgo = new Date();
      weekAgo.setDate(weekAgo.getDate() - 7);
      return finishedDate >= weekAgo;
    }).length
  };

  // Calculate streak (consecutive days with completed items)
  const calculateStreak = () => {
    const completedItems = watchlistItems
      .filter((item: any) => item.finishedAt)
      .sort((a: any, b: any) => new Date(b.finishedAt).getTime() - new Date(a.finishedAt).getTime());

    if (completedItems.length === 0) return 0;

    let streak = 0;
    let currentDate = new Date();
    currentDate.setHours(0, 0, 0, 0);

    for (let i = 0; i < completedItems.length; i++) {
      const itemDate = new Date(completedItems[i].finishedAt);
      itemDate.setHours(0, 0, 0, 0);

      const daysDiff = Math.floor((currentDate.getTime() - itemDate.getTime()) / (1000 * 60 * 60 * 24));

      if (daysDiff === streak) {
        streak++;
      } else if (daysDiff === streak + 1) {
        streak++;
      } else {
        break;
      }
    }

    return streak;
  };

  const streak = calculateStreak();

  // Filter items
  const filteredItems = watchlistItems.filter((item: any) => {
    if (filterStatus !== "all" && item.status !== filterStatus) return false;
    if (filterType !== "all" && item.type !== filterType) return false;
    return true;
  });

  // Get suggestions (random 3-5 items from "To Watch")
  const suggestions = watchlistItems
    .filter((item: any) => item.status === "To Watch")
    .sort(() => Math.random() - 0.5)
    .slice(0, Math.min(5, Math.max(3, Math.floor(watchlistItems.length / 4))));

  const getStatusColor = (status: string) => {
    switch (status) {
      case "To Watch": return "bg-blue-100 text-blue-800";
      case "In Progress": return "bg-yellow-100 text-yellow-800";
      case "Done": return "bg-green-100 text-green-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "movie": return "ðŸŽ¬";
      case "show": return "ðŸ“º";
      case "podcast": return "ðŸŽ§";
      case "other": return "ðŸ“±";
      default: return "ðŸ“";
    }
  };

  const handleExport = async () => {
    try {
      const response = await fetch('/api/watchlist/export');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'watchlist-data.csv';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
      toast({ title: "Export complete!", description: "Watchlist exported to CSV." });
    } catch (error) {
      toast({ title: "Export failed", description: "Could not export watchlist data.", variant: "destructive" });
    }
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Play className="h-8 w-8 text-green-600" />
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Watchlist</h1>
            <p className="text-gray-600">Organize and track your entertainment</p>
          </div>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={handleExport} data-testid="export-watchlist">
            <Download className="h-4 w-4 mr-2" />
            Export CSV
          </Button>
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="add-item-button">
                <Plus className="h-4 w-4 mr-2" />
                Add Item
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Add to Watchlist</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label>Title *</Label>
                  <Input 
                    value={newItem.title}
                    onChange={(e) => setNewItem({...newItem, title: e.target.value})}
                    placeholder="Enter title"
                    data-testid="input-title"
                  />
                </div>
                <div>
                  <Label>Type *</Label>
                  <Select value={newItem.type} onValueChange={(value) => setNewItem({...newItem, type: value})}>
                    <SelectTrigger data-testid="select-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="movie">Movie</SelectItem>
                      <SelectItem value="show">TV Show</SelectItem>
                      <SelectItem value="podcast">Podcast</SelectItem>
                      <SelectItem value="other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label>Source/Platform</Label>
                  <Input 
                    value={newItem.source}
                    onChange={(e) => setNewItem({...newItem, source: e.target.value})}
                    placeholder="Netflix, YouTube, etc."
                    data-testid="input-source"
                  />
                </div>
                <div>
                  <Label>Link</Label>
                  <Input 
                    value={newItem.link}
                    onChange={(e) => setNewItem({...newItem, link: e.target.value})}
                    placeholder="URL (optional)"
                    data-testid="input-link"
                  />
                </div>
                <div>
                  <Label>Runtime (minutes)</Label>
                  <Input 
                    type="number"
                    value={newItem.length}
                    onChange={(e) => setNewItem({...newItem, length: e.target.value})}
                    placeholder="Optional"
                    data-testid="input-length"
                  />
                </div>
                <div>
                  <Label>Notes</Label>
                  <Textarea 
                    value={newItem.notes}
                    onChange={(e) => setNewItem({...newItem, notes: e.target.value})}
                    placeholder="Optional notes"
                    data-testid="input-notes"
                  />
                </div>
                <Button 
                  onClick={() => addItemMutation.mutate({
                    ...newItem,
                    length: newItem.length ? parseInt(newItem.length) : null
                  })}
                  disabled={!newItem.title || !newItem.type || addItemMutation.isPending}
                  className="w-full"
                  data-testid="save-item"
                >
                  {addItemMutation.isPending ? "Adding..." : "Add to Watchlist"}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{stats.total}</div>
            <div className="text-sm text-gray-600">Total Items</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-yellow-600">{stats.toWatch}</div>
            <div className="text-sm text-gray-600">To Watch</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-orange-600">{stats.inProgress}</div>
            <div className="text-sm text-gray-600">In Progress</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-green-600">{stats.done}</div>
            <div className="text-sm text-gray-600">Completed</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-purple-600">{stats.thisWeek}</div>
            <div className="text-sm text-gray-600">This Week</div>
          </CardContent>
        </Card>
      </div>

      {/* Suggestions */}
      {suggestions.length > 0 && (
        <Card className="border-green-200 bg-green-50">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Star className="h-5 w-5 text-green-600" />
              Suggested for You
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {suggestions.slice(0, 3).map((item: any) => (
                <div key={item.id} className="p-3 bg-white rounded-lg border">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="font-medium">{getTypeIcon(item.type)} {item.title}</div>
                      {item.source && (
                        <div className="text-sm text-gray-600 mt-1">{item.source}</div>
                      )}
                      {item.length && (
                        <div className="text-sm text-gray-600">{item.length} min</div>
                      )}
                    </div>
                    <Button 
                      size="sm" 
                      onClick={() => handleMarkDone(item)}
                      disabled={updateItemMutation.isPending}
                      data-testid={`mark-done-${item.id}`}
                    >
                      <CheckCircle2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Filter className="h-5 w-5" />
            Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex gap-4">
            <div>
              <Label>Status</Label>
              <Select value={filterStatus} onValueChange={setFilterStatus}>
                <SelectTrigger className="w-32" data-testid="filter-status">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All</SelectItem>
                  <SelectItem value="To Watch">To Watch</SelectItem>
                  <SelectItem value="In Progress">In Progress</SelectItem>
                  <SelectItem value="Done">Done</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label>Type</Label>
              <Select value={filterType} onValueChange={setFilterType}>
                <SelectTrigger className="w-32" data-testid="filter-type">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All</SelectItem>
                  <SelectItem value="movie">Movies</SelectItem>
                  <SelectItem value="show">TV Shows</SelectItem>
                  <SelectItem value="podcast">Podcasts</SelectItem>
                  <SelectItem value="other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Watchlist Items */}
      <Card>
        <CardHeader>
          <CardTitle>Your Watchlist ({filteredItems.length} items)</CardTitle>
        </CardHeader>
        <CardContent>
          {filteredItems.length > 0 ? (
            <div className="space-y-3">
              {filteredItems.map((item: any) => (
                <div key={item.id} className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg">
                  <div className="text-2xl">{getTypeIcon(item.type)}</div>

                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <span className="font-medium">{item.title}</span>
                      <Badge className={getStatusColor(item.status)}>
                        {item.status}
                      </Badge>
                    </div>

                    <div className="flex items-center gap-4 text-sm text-gray-600">
                      {item.source && <span>{item.source}</span>}
                      {item.length && <span>{item.length} min</span>}
                      {item.link && (
                        <a 
                          href={item.link} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="flex items-center gap-1 text-blue-600 hover:underline"
                        >
                          <LinkIcon className="h-3 w-3" />
                          Link
                        </a>
                      )}
                    </div>

                    {item.notes && (
                      <div className="text-sm text-gray-600 mt-1">{item.notes}</div>
                    )}

                    {item.finishedAt && (
                      <div className="text-xs text-gray-500 mt-1">
                        Completed on {new Date(item.finishedAt).toLocaleDateString()}
                      </div>
                    )}
                  </div>

                  <div className="flex items-center gap-2">
                    {item.status !== "Done" && (
                      <div className="flex gap-1">
                        {item.status === "To Watch" && (
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => handleStatusChange(item, "In Progress")}
                            data-testid={`start-${item.id}`}
                          >
                            Start
                          </Button>
                        )}
                        <Button 
                          size="sm"
                          onClick={() => handleMarkDone(item)}
                          disabled={updateItemMutation.isPending}
                          data-testid={`mark-done-${item.id}`}
                        >
                          <CheckCircle2 className="h-4 w-4" />
                        </Button>
                      </div>
                    )}

                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setEditingItem(item)}
                      data-testid={`edit-${item.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>

                    <Button
                      size="sm"
                      variant="destructive"
                      onClick={() => deleteItemMutation.mutate(item.id)}
                      disabled={deleteItemMutation.isPending}
                      data-testid={`delete-${item.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <Play className="h-12 w-12 mx-auto mb-4 opacity-30" />
              <p>No items found</p>
              <p className="text-sm">
                {watchlistItems.length === 0 
                  ? "Add your first movie, show, or podcast to get started"
                  : "Try adjusting your filters"
                }
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Dialog */}
      <Dialog open={!!editingItem} onOpenChange={() => setEditingItem(null)}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Item</DialogTitle>
          </DialogHeader>
          {editingItem && (
            <div className="space-y-4">
              <div>
                <Label>Title</Label>
                <Input 
                  value={editingItem.title}
                  onChange={(e) => setEditingItem({...editingItem, title: e.target.value})}
                  data-testid="edit-title"
                />
              </div>
              <div>
                <Label>Status</Label>
                <Select value={editingItem.status} onValueChange={(value) => setEditingItem({...editingItem, status: value})}>
                  <SelectTrigger data-testid="edit-status">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="To Watch">To Watch</SelectItem>
                    <SelectItem value="In Progress">In Progress</SelectItem>
                    <SelectItem value="Done">Done</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label>Source/Platform</Label>
                <Input 
                  value={editingItem.source || ""}
                  onChange={(e) => setEditingItem({...editingItem, source: e.target.value})}
                  data-testid="edit-source"
                />
              </div>
              <div>
                <Label>Notes</Label>
                <Textarea 
                  value={editingItem.notes || ""}
                  onChange={(e) => setEditingItem({...editingItem, notes: e.target.value})}
                  data-testid="edit-notes"
                />
              </div>
              <Button 
                onClick={() => updateItemMutation.mutate({
                  id: editingItem.id,
                  updates: editingItem
                })}
                disabled={updateItemMutation.isPending}
                className="w-full"
                data-testid="save-edit"
              >
                {updateItemMutation.isPending ? "Saving..." : "Save Changes"}
              </Button>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/weekly-plan.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Plus, Calendar, Target, CheckCircle2, Circle, 
  Star, Clock, ArrowLeft, ArrowRight, Edit
} from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { format, startOfWeek, addWeeks, subWeeks } from "date-fns";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { WeeklyPlan, InsertWeeklyPlan } from "@shared/schema";

const weeklyPlanFormSchema = z.object({
  title: z.string().min(1, "Title is required"),
  goals: z.array(z.object({
    id: z.string(),
    text: z.string().min(1, "Goal text is required"),
    completed: z.boolean().default(false),
    category: z.string().min(1, "Category is required")
  })).optional(),
  priorities: z.array(z.object({
    id: z.string(),
    text: z.string().min(1, "Priority text is required"),
    category: z.string().min(1, "Category is required"),
    completed: z.boolean().default(false)
  })).optional(),
  notes: z.string().optional(),
  reflection: z.string().optional()
});

type WeeklyPlanFormData = z.infer<typeof weeklyPlanFormSchema>;

export default function WeeklyPlanPage() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedWeek, setSelectedWeek] = useState(new Date());
  const [newGoal, setNewGoal] = useState("");
  const [newGoalCategory, setNewGoalCategory] = useState("");
  const [newPriority, setNewPriority] = useState("");
  const [newPriorityCategory, setNewPriorityCategory] = useState("");
  const { toast } = useToast();

  const weekStartDate = format(startOfWeek(selectedWeek, { weekStartsOn: 1 }), "yyyy-MM-dd");

  const { data: weeklyPlan, isLoading } = useQuery({
    queryKey: ["/api/weekly-plans", { weekStartDate }],
    queryFn: () => apiRequest(`/api/weekly-plans?weekStartDate=${weekStartDate}`, "GET")
  });

  const { data: allPlans = [] } = useQuery({
    queryKey: ["/api/weekly-plans"],
  });

  const form = useForm<WeeklyPlanFormData>({
    resolver: zodResolver(weeklyPlanFormSchema),
    defaultValues: {
      title: `Week of ${format(startOfWeek(selectedWeek, { weekStartsOn: 1 }), "MMM dd, yyyy")}`,
      goals: [],
      priorities: [],
      notes: "",
      reflection: ""
    }
  });

  const createPlanMutation = useMutation({
    mutationFn: (data: WeeklyPlanFormData) => 
      apiRequest("/api/weekly-plans", "POST", {
        ...data,
        weekStartDate
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/weekly-plans"] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Weekly plan created successfully!"
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create weekly plan",
        variant: "destructive"
      });
    }
  });

  const updatePlanMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<WeeklyPlan> }) =>
      apiRequest(`/api/weekly-plans/${id}`, "PUT", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/weekly-plans"] });
      toast({
        title: "Success",
        description: "Weekly plan updated!"
      });
    }
  });

  const onSubmit = (data: WeeklyPlanFormData) => {
    createPlanMutation.mutate(data);
  };

  const addGoal = () => {
    if (!newGoal || !newGoalCategory) return;

    const currentGoals = form.getValues("goals") || [];
    const newGoalItem = {
      id: Math.random().toString(36).substr(2, 9),
      text: newGoal,
      completed: false,
      category: newGoalCategory
    };

    form.setValue("goals", [...currentGoals, newGoalItem]);
    setNewGoal("");
    setNewGoalCategory("");
  };

  const addPriority = () => {
    if (!newPriority || !newPriorityCategory) return;

    const currentPriorities = form.getValues("priorities") || [];
    const newPriorityItem = {
      id: Math.random().toString(36).substr(2, 9),
      text: newPriority,
      category: newPriorityCategory,
      completed: false
    };

    form.setValue("priorities", [...currentPriorities, newPriorityItem]);
    setNewPriority("");
    setNewPriorityCategory("");
  };

  const toggleGoal = (goalId: string) => {
    if (!weeklyPlan) return;

    const updatedGoals = (weeklyPlan.goals || []).map((goal: any) => 
      goal.id === goalId ? { ...goal, completed: !goal.completed } : goal
    );

    updatePlanMutation.mutate({
      id: weeklyPlan.id,
      data: { goals: updatedGoals }
    });
  };

  const togglePriority = (priorityId: string) => {
    if (!weeklyPlan) return;

    const updatedPriorities = (weeklyPlan.priorities || []).map((priority: any) => 
      priority.id === priorityId ? { ...priority, completed: !priority.completed } : priority
    );

    updatePlanMutation.mutate({
      id: weeklyPlan.id,
      data: { priorities: updatedPriorities }
    });
  };

  const navigateWeek = (direction: "prev" | "next") => {
    setSelectedWeek(prev => direction === "prev" ? subWeeks(prev, 1) : addWeeks(prev, 1));
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/4 mb-6"></div>
          <div className="grid gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-32 bg-gray-200 dark:bg-gray-700 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="weekly-plan-page">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Weekly Planning</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            Plan your week with goals and priorities
          </p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-plan">
              <Plus className="mr-2 h-4 w-4" />
              Create Plan
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create Weekly Plan</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Plan Title</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-plan-title" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-900 dark:text-white">
                      Weekly Goals
                    </label>
                    <div className="mt-2 space-y-2">
                      <div className="flex gap-2">
                        <Input
                          placeholder="Add a goal..."
                          value={newGoal}
                          onChange={(e) => setNewGoal(e.target.value)}
                          data-testid="input-new-goal"
                        />
                        <Input
                          placeholder="Category"
                          value={newGoalCategory}
                          onChange={(e) => setNewGoalCategory(e.target.value)}
                          className="w-32"
                          data-testid="input-goal-category"
                        />
                        <Button 
                          type="button" 
                          onClick={addGoal}
                          data-testid="button-add-goal"
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                      <div className="space-y-1">
                        {(form.watch("goals") || []).map((goal, index) => (
                          <div key={goal.id} className="flex items-center gap-2 p-2 bg-gray-50 dark:bg-gray-800 rounded">
                            <Badge variant="outline">{goal.category}</Badge>
                            <span className="flex-1">{goal.text}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  <div>
                    <label className="text-sm font-medium text-gray-900 dark:text-white">
                      Weekly Priorities
                    </label>
                    <div className="mt-2 space-y-2">
                      <div className="flex gap-2">
                        <Input
                          placeholder="Add a priority..."
                          value={newPriority}
                          onChange={(e) => setNewPriority(e.target.value)}
                          data-testid="input-new-priority"
                        />
                        <Input
                          placeholder="Category"
                          value={newPriorityCategory}
                          onChange={(e) => setNewPriorityCategory(e.target.value)}
                          className="w-32"
                          data-testid="input-priority-category"
                        />
                        <Button 
                          type="button" 
                          onClick={addPriority}
                          data-testid="button-add-priority"
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                      <div className="space-y-1">
                        {(form.watch("priorities") || []).map((priority, index) => (
                          <div key={priority.id} className="flex items-center gap-2 p-2 bg-gray-50 dark:bg-gray-800 rounded">
                            <Badge variant="outline">{priority.category}</Badge>
                            <span className="flex-1">{priority.text}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>

                <FormField
                  control={form.control}
                  name="notes"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Notes</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Additional notes..." {...field} data-testid="textarea-notes" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={createPlanMutation.isPending}
                  data-testid="button-submit-plan"
                >
                  {createPlanMutation.isPending ? "Creating..." : "Create Weekly Plan"}
                </Button>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Week Navigation */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <Button 
              variant="outline" 
              onClick={() => navigateWeek("prev")}
              data-testid="button-prev-week"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Previous Week
            </Button>

            <div className="text-center">
              <h2 className="text-lg font-semibold text-gray-900 dark:text-white">
                Week of {format(startOfWeek(selectedWeek, { weekStartsOn: 1 }), "MMM dd, yyyy")}
              </h2>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                {format(startOfWeek(selectedWeek, { weekStartsOn: 1 }), "MMM dd")} - {format(new Date(startOfWeek(selectedWeek, { weekStartsOn: 1 }).getTime() + 6 * 24 * 60 * 60 * 1000), "MMM dd")}
              </p>
            </div>

            <Button 
              variant="outline" 
              onClick={() => navigateWeek("next")}
              data-testid="button-next-week"
            >
              Next Week
              <ArrowRight className="h-4 w-4 ml-2" />
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Current Week Plan */}
      {weeklyPlan ? (
        <div className="space-y-6">
          {/* Plan Overview */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5" />
                {weeklyPlan.title}
              </CardTitle>
            </CardHeader>
            <CardContent>
              {weeklyPlan.notes && (
                <p className="text-gray-600 dark:text-gray-400">{weeklyPlan.notes}</p>
              )}

              <div className="grid gap-4 md:grid-cols-2 mt-4">
                <div>
                  <h3 className="font-medium text-gray-900 dark:text-white mb-2">Progress</h3>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span>Goals Completed</span>
                      <span>{(weeklyPlan.goals || []).filter((g: any) => g.completed).length} / {(weeklyPlan.goals || []).length}</span>
                    </div>
                    <div className="flex justify-between text-sm">
                      <span>Priorities Completed</span>
                      <span>{(weeklyPlan.priorities || []).filter((p: any) => p.completed).length} / {(weeklyPlan.priorities || []).length}</span>
                    </div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Goals and Priorities */}
          <div className="grid gap-6 md:grid-cols-2">
            {/* Goals */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Target className="h-5 w-5" />
                  Weekly Goals
                </CardTitle>
              </CardHeader>
              <CardContent>
                {!weeklyPlan.goals || weeklyPlan.goals.length === 0 ? (
                  <p className="text-gray-500 dark:text-gray-400">No goals set for this week</p>
                ) : (
                  <div className="space-y-3">
                    {weeklyPlan.goals.map((goal: any) => (
                      <div 
                        key={goal.id}
                        className="flex items-center gap-3 p-3 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                      >
                        <button 
                          onClick={() => toggleGoal(goal.id)}
                          data-testid={`button-toggle-goal-${goal.id}`}
                        >
                          {goal.completed ? (
                            <CheckCircle2 className="h-5 w-5 text-green-600" />
                          ) : (
                            <Circle className="h-5 w-5 text-gray-400" />
                          )}
                        </button>
                        <div className="flex-1">
                          <p className={`${goal.completed ? "line-through text-gray-500" : "text-gray-900 dark:text-white"}`}>
                            {goal.text}
                          </p>
                          <Badge variant="outline" className="text-xs mt-1">
                            {goal.category}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Priorities */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Star className="h-5 w-5" />
                  Weekly Priorities
                </CardTitle>
              </CardHeader>
              <CardContent>
                {!weeklyPlan.priorities || weeklyPlan.priorities.length === 0 ? (
                  <p className="text-gray-500 dark:text-gray-400">No priorities set for this week</p>
                ) : (
                  <div className="space-y-3">
                    {weeklyPlan.priorities.map((priority: any) => (
                      <div 
                        key={priority.id}
                        className="flex items-center gap-3 p-3 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                      >
                        <button 
                          onClick={() => togglePriority(priority.id)}
                          data-testid={`button-toggle-priority-${priority.id}`}
                        >
                          {priority.completed ? (
                            <CheckCircle2 className="h-5 w-5 text-green-600" />
                          ) : (
                            <Circle className="h-5 w-5 text-gray-400" />
                          )}
                        </button>
                        <div className="flex-1">
                          <p className={`${priority.completed ? "line-through text-gray-500" : "text-gray-900 dark:text-white"}`}>
                            {priority.text}
                          </p>
                          <Badge variant="outline" className="text-xs mt-1">
                            {priority.category}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Reflection */}
          {weeklyPlan.reflection && (
            <Card>
              <CardHeader>
                <CardTitle>Weekly Reflection</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-600 dark:text-gray-400">{weeklyPlan.reflection}</p>
              </CardContent>
            </Card>
          )}
        </div>
      ) : (
        <Card>
          <CardContent className="p-8 text-center">
            <Calendar className="mx-auto h-12 w-12 text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
              No plan for this week
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              Create a weekly plan to set goals and priorities for this week.
            </p>
            <Button onClick={() => setIsDialogOpen(true)} data-testid="button-create-first-plan">
              <Plus className="mr-2 h-4 w-4" />
              Create Weekly Plan
            </Button>
          </CardContent>
        </Card>
      )}

      {/* Previous Plans */}
      {allPlans.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Previous Weekly Plans</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {allPlans.slice(0, 5).map((plan: WeeklyPlan) => (
                <div 
                  key={plan.id}
                  className="flex items-center justify-between p-3 border rounded-lg"
                >
                  <div>
                    <h4 className="font-medium text-gray-900 dark:text-white">{plan.title}</h4>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      Week of {format(new Date(plan.weekStartDate), "MMM dd, yyyy")}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {(plan.goals || []).filter((g: any) => g.completed).length}/{(plan.goals || []).length} goals completed
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

